package realtimeprgm;
//counting of how many odd or even numbers in given
public class Countt {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int count=0;
for(int i=1;i<=30;i++) {
	if(i%2==1)//odd numbers
		//if(i%2==0)
	{
		count++;
	}
}
System.out.println(count);
	}

}
